import React, { Component } from 'react';
import Autosuggest from 'react-autosuggest';
import {cities} from './variables.js';
import Forecast from './Components/forecast';


export var city = 'Croydon';

export var country = 'GB'

export const Api_Key = "8c5021109e1918aa1c478ce6b4f53c27";


function hasName(prop, value, data) {
  return data.some(function(obj) {
      return prop in obj && obj[prop] === value;
  });
}


function escapeRegexCharacters(str) {
  return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

function getSuggestions(value) {
  const escapedValue = escapeRegexCharacters(value.trim());

  if (escapedValue === '') {
    return [];
  }

  const regex = new RegExp('^' + escapedValue, 'i');

  return cities
    .map(section => {
      return {
        title: section.title,
        cities: section.cities.filter(language => regex.test(language.name))
      };
    })
    .filter(section => section.cities.length > 0);
}

function getSuggestionValue(suggestion) {
  return suggestion.name;
}

function renderSuggestion(suggestion) {
  return (
    <span>{suggestion.name}</span>
  );
}

function renderSectionTitle(section) {
  return (
    <strong>{section.title}</strong>
  );
}

function getSectionSuggestions(section) {
  return section.cities;
}
function getTitle(cities, prop, value) {
  // Find the language...
  var result = cities.find(function (language) {
    // ... where some item has item[prop] === value
    return language.cities.some(function (item) {
      return item[prop] === value
    })
  })

  return result ? result.title : null
}



class App extends React.Component {

  state = {

    temperature: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    error: undefined,
    value: '',
    suggestions: [],
    message: undefined,
    subMessage: undefined

  }



  //getWeather is a method we'll use to make the api call
  getWeather = async (e) => {

    //city = 'Croydon'; //e.target.elements.city.value;
    //var country = 'GB'; //e.target.elements.country.value;
    //e.preventDefault();   
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&units=metric&appid=${Api_Key}`); // fetch("http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=8c5021109e1918aa1c478ce6b4f53c27");// fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${Api_Key}`);
    const response = await api_call.json();
    console.log(response);
    if(city && country){
      this.setState({
        temperature: response.main.temp,
        city: response.name,
        country: response.sys.country,
        humidity: response.main.humidity,
        description: response.weather[0].description,
        icon: response.weather[0].icon,
        error: ""
      })
      if (this.state.icon === '01d') {
        if (this.state.temp > 18) {
          this.setState({
            message: 'Fucking Lush',
            subMessage: "If you had friends you could go outside and play!"
          })
        } else {
          this.setState({
            message: 'Pretty, but dissapointingly cold.',
            subMessage: "Like you, except it's pretty."
          })
        }
      }
      if (this.state.icon === '02d' || '02n') {
        this.setState({
          message: 'Not bad',
          subMessage: "Unlike you. You're really bad."
        })
      }
      if (this.state.icon === '03d' || '03n') {
        this.setState({
          message: 'Fucking unremarkable out there.',
          subMessage: "Have you been giving the weather life advice? Please stop."
        })
      }
      if (this.state.icon === '03d' || '03n') {
        this.setState({
          message: "It's fucking freezing.",
          subMessage: "Fuck you, cold-face."
        })
      }
      if (this.state.icon === '04d' || '04n') {
        this.setState({
          message: 'Broken sky, broken dreams.',
          subMessage: "Just keep pretending everything's ok."
        })
      }
      if (this.state.icon === '09d' || '09n') {
        this.setState({
          message: 'Proper fucking raining out there.',
          subMessage: "Go outside, you need a shower."
        })
      }
      if (this.state.icon === '10d' || '10d') {
        this.setState({
          message: 'Rain, rain, go away.',
          subMessage: "And stay away, you mouth-breathing trogolodyte."
        })
      }
      if (this.state.icon === '11d' || '11n') {
        this.setState({
          message: 'BAM! KAPOW!',
          subMessage: "Raaaaarrrrrgggghhh! I'm running on thunder, motherfucker!"
        })
      }
      if (this.state.icon === '13d') {
        this.setState({
          message: 'Snow Day!',
          subMessage: "At least today you have an excuse for staying in and doing nothing, loser."
        })
      }
      if (this.state.icon === '13n') {
        this.setState({
          message: 'Snow Night!',
          subMessage: "Remember people can see your footprints in the snow, pervert."
        })
      }
      if (this.state.icon === '50d') {
        this.setState({
          message: 'Gorilla in the mist',
          subMessage: "You **are** disgustingly hairy."
        })
      }
    }
    else {
      this.setState({
        error: "Please input search values..."
      })
    }
  }


  getForecast = async (e) => {

    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&units=metric&appid=${Api_Key}`); // fetch("http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=8c5021109e1918aa1c478ce6b4f53c27");// fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${Api_Key}`);
    var response = await api_call.json();
    //var currentForecast = this.props.currentForecast;
    var i = 0;
    console.log('forecast');
    console.log(response);
    if(city && country){
      this.setState({
        currentForecast: response
      })
    }
  }


  componentDidMount() {
    this.getWeather()
    this.getForecast()
  };

  onChange = (event, { newValue, method }) => {
    this.setState({
      value: newValue
    });


    function hasValue(cities, prop, value) {
      return cities.some(function (language) {
        return language.cities.some(function (item) {
          return item[prop] === value
        })
      })
    }

    if (hasValue(cities, 'name', newValue)) {

      city = newValue;
      country = getTitle(cities, 'name', newValue);
      this.getWeather()
      this.getForecast()

    }

  };

  onSuggestionsFetchRequested = ({ value }) => {
    this.setState({
      suggestions: getSuggestions(value)
    });
  };

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  render() {
    const { value, suggestions } = this.state;
    const inputProps = {
      placeholder: "Where are we checking?",
      value,
      onChange: this.onChange
    };


    return (

      <div>
         <div className="wrapper">
          <div className="main">
            <div className="container">
              <div className="row">
                <div className="col-xs-5 title-container">
                    <Autosuggest 
                      multiSection={true}
                      suggestions={suggestions}
                      onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                      onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                      getSuggestionValue={getSuggestionValue}
                      renderSuggestion={renderSuggestion}
                      renderSectionTitle={renderSectionTitle}
                      getSectionSuggestions={getSectionSuggestions}
                      inputProps={inputProps} 
                    />
                </div>
                <div className="now">
                <img src={"http://openweathermap.org/img/w/" + this.state.icon + ".png"} />
                <h1>{Math.floor(this.state.temperature)}Â°</h1>
                <h2>{this.state.description}</h2>
                <h3>{this.state.city}, {this.state.country}</h3>
                <h1>{this.state.message}</h1>
                <h4>{this.state.subMessage}</h4>
                </div>
                {this.state.currentForecast ? <Forecast currentForecast={this.state.currentForecast} /> : null}
              </div>
            </div>
          </div>
        </div>
      </div>

    )
  }
}
export default App;
