          })
        }
      }
    }
    else {
      this.setState({
        error: "Please input search values..."
      })
    }
  }


  getForecast = async (e) => {

    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/forecast?q=${city},${country}&units=metric&appid=${Api_Key}`); // fetch("http://api.openweathermap.org/data/2.5/weather?q=London,uk&appid=8c5021109e1918aa1c478ce6b4f53c27");// fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${Api_Key}`);
    var response = await api_call.json();
    //var currentForecast = this.props.currentForecast;
    var i = 0;
    console.log('forecast');
    console.log(response);
    if(city && country){
      this.setState({
        currentForecast: response
      })
    }
  }


  componentDidMount() {
    this.getWeather()
    this.getForecast()
  };

  onChange = (event, { newValue, method }) => {
    this.setState({
      value: newValue
    });


    function hasValue(cities, prop, value) {
      return cities.some(function (language) {
        return language.cities.some(function (item) {
          return item[prop] === value
        })
      })
    }

    if (hasValue(cities, 'name', newValue)) {

      city = newValue;
      country = getTitle(cities, 'name', newValue);
      this.getWeather()
      this.getForecast()

    }

  };

  onSuggestionsFetchRequested = ({ value }) => {
    this.setState({
      suggestions: getSuggestions(value)
    });
  };

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  render() {
    const { value, suggestions } = this.state;
    const inputProps = {
      placeholder: "Where are we checking?",
      value,
      onChange: this.onChange
    };


    return (

      <div>
         <div className="wrapper">
          <div className="main">
            <div className="container">
              <div className="row">
                <div className="col-xs-5 title-container">
                    <Autosuggest 
                      multiSection={true}
                      suggestions={suggestions}
                      onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                      onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                      getSuggestionValue={getSuggestionValue}
                      renderSuggestion={renderSuggestion}
                      renderSectionTitle={renderSectionTitle}
                      getSectionSuggestions={getSectionSuggestions}
                      inputProps={inputProps} 
                    />
                </div>
                <div className="now">
                  <img src={"http://openweathermap.org/img/w/" + this.state.icon + ".png"} />
                  <h1>{Math.floor(this.state.temperature)}°</h1>
                  <p>{this.state.description}.</p>
                  <h1>{this.state.message}</h1>
                  <h4>{this.state.subMessage}</h4>
                </div>
                <div className="weatherRow">
                  {this.state.currentForecast ? <Forecast currentForecast={this.state.currentForecast} /> : null}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

    )
  }
}
export default App;
