    render() {
        var i = 0;
            return (
                this.props.currentForecast.list.map(item => (

                        <div className="weatherTile" key={item.dt_txt}>
                            <p>{item.dt}</p>
                            <p>{item.main.temp}</p>
                            <p>{item.weather[i].description}</p>
                        </div>
                    ))
            )
    }
}

export default Forecast
