        //     }
        //     var dateStr = [day, hour].join(" "); 
        //     this.setState({
        //             [i]: dateStr,
        //     })
        // } 

    }

    render() {
            return (
                this.props.currentForecast.list.map(item => (

                        <div className="weatherTile" key={item.dt_txt}>
                            <p>{item.dt}</p>
                            <img src={"http://openweathermap.org/img/w/" + item.weather[0].icon + ".png"} />
                            <p>{item.main.temp}</p>
                            <p>{item.weather[0].description}</p>
                            <img className="windArrow" src={require('../img/arrow.svg')} style={{transform: `rotate(${item.wind.deg}deg)`, width: `20px`}} />
                            <p>{item.wind.speed}mph</p>
                        </div>
                    ))
            )
    }
}


