import React from 'react'

var currentForecast = undefined;

class Forecast extends React.Component {
    constructor(props) {
        super(props);
        this.setState ({
            currentForecast: this.props.currentForecast
        })
        var currentForecast = this.props.currentForecast;
        for(let i = 0; i<currentForecast.list.length;  i+=1) {
            console.log(currentForecast.list[i].dt)
            var secondsSinceEpoch = currentForecast.list[i].dt;
            var date = new Date(secondsSinceEpoch * 1000);
            var days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
            var day = days[date.getUTCDay()];
            var utcHour = date.getUTCHours();
            var hour = utcHour + (utcHour >= 12 ? "pm": "am");
            var dateStr = [day, hour].join(" "); // Wednesday 12pm
            console.log(dateStr);
            this.setState({
                    [i]: dateStr
            })
        } 
    }

    render() {
        var currentForecast = this.props.currentForecast
        for(let i = 0; i<currentForecast.list.length;  i+=1) {
            return (
                currentForecast.list.map(item => (
                    <div className="weatherTile" key={item.dt_txt}>
                        <p>{item.dt_txt}</p>
                        <p>{JSON.stringify(new Date(item.dt * 1000))}</p>
                    </div>
                ))
            )
        } 
    }
}

export default Forecast
