(function() {
  var AnsiPainter, paint;

  AnsiPainter = require('../src/AnsiPainter');

  paint = function(t) {
    return AnsiPainter.paint(t);
  };

  describe("AnsiPainter", function() {
    describe("paint()", function() {
      it("should handle basic coloring", function() {
        var t;
        t = "<bg-white><black>a</black></bg-white>";
        return paint(t).should.equal('\u001b[30m\u001b[47ma\u001b[0m');
      });
      it("should handle color in color", function() {
        var t;
        t = "<red>a<blue>b</blue></red>";
        return paint(t).should.equal('\u001b[31ma\u001b[0m\u001b[34mb\u001b[0m');
      });
      return it("should skip empty tags", function() {
        var t;
        t = "<blue></blue>a";
        return paint(t).should.equal('a\u001b[0m');
      });
    });
    return describe("_replaceSpecialStrings()", function() {
      return it("should work", function() {
        return AnsiPainter.prototype._replaceSpecialStrings('&lt;&gt;&quot;&sp;&amp;').should.equal('<>" &');
      });
    });
  });

}).call(this);
