(function() {
  var Layout;

  Layout = require('../src/Layout');

  describe("Layout", function() {
    describe("constructor()", function() {
      return it("should create root block", function() {
        var l;
        l = new Layout;
        expect(l._root).to.exist;
        return l._root._name.should.equal('__root');
      });
    });
    return describe("get()", function() {
      return it("should not be allowed when any block is open", function() {
        var l;
        l = new Layout;
        l.openBlock();
        return (function() {
          return l.get();
        }).should["throw"](Error);
      });
    });
  });

}).call(this);
