(function() {
  var StyleSheet;

  StyleSheet = require('../../../src/renderKid/styles/StyleSheet');

  describe("StyleSheet", function() {
    return describe("normalizeSelector()", function() {
      return it('should remove unnecessary spaces', function() {
        return StyleSheet.normalizeSelector(' body+a   s >   a ').should.equal('body+a s>a');
      });
    });
  });

}).call(this);
