(function() {
  var tools;

  tools = require('../src/tools');

  describe("tools", function() {
    describe("quote()", function() {
      return it("should convert html special strings to their entities", function() {
        return tools.quote(" abc<>\"\n").should.equal('&sp;abc&lt;&gt;&quot;<br />');
      });
    });
    describe("stringToDom()", function() {
      return it("should work", function() {
        return tools.stringToDom('<a> text<a1>text</a1> text <a2>text</a2><a3>text</a3>text</a>text');
      });
    });
    return describe("objectToDom()", function() {
      it("should work", function() {
        return tools.objectToDom({
          a: 'text'
        });
      });
      return it("should have quoted text nodes", function() {
        return tools.objectToDom({
          a: '&<> "'
        })[0].children[0].data.should.equal('&amp;&lt;&gt;&sp;&quot;');
      });
    });
  });

}).call(this);
